    public static boolean validate(String emailStr) {
        Matcher matcher = VALID_EMAIL_ADDRESS_REGEX.matcher(emailStr);
        return matcher.find();
    }

    public static void fixSize(View view) {
        Drawable d = ((ImageView)view).getDrawable();
        float width = d.getBounds().width();
        float height = d.getBounds().height();
        float retio =((View)view.getParent()).getWidth() / width;
        LinearLayout.LayoutParams layoutParams = new LinearLayout.LayoutParams((int)(width*retio),((int)(height*retio)));
        view.setLayoutParams(layoutParams);
    }

    public static void hideKeyboard(Context c){
        Activity a = (Activity) c;
        View v = a.getCurrentFocus();
        if (v != null) {
            InputMethodManager imm = (InputMethodManager)c.getSystemService(Context.INPUT_METHOD_SERVICE);
            imm.hideSoftInputFromWindow(v.getWindowToken(), 0);
        }
    }

    public static void ShowKeyboard(Context c , View v){
        InputMethodManager imm = (InputMethodManager) c.getSystemService(Context.INPUT_METHOD_SERVICE);
        imm.showSoftInput(v, InputMethodManager.SHOW_IMPLICIT);
    }

    public static void clearFocus(Activity a){
        if (a.getCurrentFocus()!=null)
            a.getCurrentFocus().clearFocus();
    }

    public static void showProgressDialog(Context context) {
        if (mProgressDialog == null) {
            mProgressDialog = new ProgressDialog(context);
            mProgressDialog.setCancelable(false);
            mProgressDialog.setMessage("Loading...");
        }

        mProgressDialog.show();
    }

    public static void hideProgressDialog() {
        if (mProgressDialog != null && mProgressDialog.isShowing()) {
            mProgressDialog.dismiss();
        }
    }

    public static void savePreferences(Activity activity , String key , String value) {
        SharedPreferences.Editor editor = activity.getSharedPreferences(PREFS, Activity.MODE_PRIVATE).edit();
        editor.putString(key,value);
        editor.apply();
    }

    public static String loadPreferences(Activity activity , String key){
        SharedPreferences prefs = activity.getSharedPreferences(U.PREFS, Activity.MODE_PRIVATE);
        return  prefs.getString(key, null);

    }

    public static void saveBoolPreferences(Activity activity , String key , boolean value) {
        SharedPreferences.Editor editor = activity.getSharedPreferences(PREFS, Activity.MODE_PRIVATE).edit();
        editor.putBoolean(key,value);
        editor.apply();
    }

    public static boolean loadBoolPreferences(Activity activity , String key){
        SharedPreferences prefs = activity.getSharedPreferences(U.PREFS, Activity.MODE_PRIVATE);
        return  prefs.getBoolean(key, false);

    }

    public static boolean isNetworkConnected(Context context) {
        ConnectivityManager cm = (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);
        return cm.getActiveNetworkInfo() != null;
    }


